package strings;

public class ConvertingNonStringTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int ival = 100;
		
		String sval = String.valueOf(ival);
		
		System.out.println(sval);
		
		int i=2,j=3;
		
		int result = i*j;
		
		System.out.println(i+ "*"+ j +"= "+result );
		
		

	}

}
