package variables;

public class NestedIf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub.0
		float students=0.0f;
		float rooms =4.0f;
		
		if(students>0.0f) {
			if(rooms>0)
				System.out.println(students/rooms);}
		else
			System.out.println("No students");
		
		
		System.out.println("End Program");

	}

}
